 } catch (e: Exception) {
 Log.e(TAG, "Error in onCountdownComplete: ${e.message}")
 mainHandler.post {
 Toast.makeText(this@MainActivity, "Error starting data stream: ${e.message}", Toast.LENGTH_LONG).show()
 currentAdminScreen = ADMIN_MAIN
 isTimerRunning = false
