 currentAuthScreen = SIGN_IN
 }
 )
 else -> {
 currentAdminScreen = ADMIN_SCAN
 }
 }
 }
 } else if (currentUser == null) {
 // No Firebase user and not fixed admin logged in, show auth screens
 Crossfade(
 targetState = currentAuthScreen,
