 if (email == ADMIN_EMAIL && password == ADMIN_PASSWORD) {
 isAdminFixedLoggedIn = true
 currentAdminScreen = ADMIN_SCAN // Navigate to Admin dashboard
 // Also, sign out any Firebase user if logged in, to avoid conflict
