 timerAccumulatedMillis += elapsed
 isTimerRunning = false
 timerStartTimeMillis = 0L
 Log.d(TAG, "Timer stopped. Accumulated: $timerAccumulatedMillis ms")
 }
 } catch (e: Exception) {
 Log.e(TAG, "Error stopping streaming: ${e.message}")
