Create the cloud function (`functions/index.js`):

```javascript
const functions = require('firebase-functions');
const admin = require('firebase-admin');
const { Storage } = require('@google-cloud/storage');
const axios = require('axios');

admin.initializeApp();

exports.processGloveData = functions.storage.object().onFinalize(async (object) => {
 const filePath = object.name;

 // Only process CSV files in sessions folder
 if (!filePath.startsWith('sessions/') || !filePath.endsWith('.csv')) {
 console.log('Skipping non-session file:', filePath);
 return;
 }

 const docId = filePath.replace('sessions/', '').replace('.csv', '');

 try {
 // Set processing status
 await admin.firestore().collection('processingResults').doc(docId).set({
 status: 'processing',
 startedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });

 // Download CSV from Firebase Storage
 const storage = new Storage();
 const bucket = storage.bucket(object.bucket);
 const file = bucket.file(filePath);

 const [csvData] = await file.download();
 console.log('Downloaded CSV file:', filePath);

 // Process with your algorithm
 const result = await processGloveDataAlgorithm(csvData.toString());

 // Store results in Firestore
 await admin.firestore().collection('processingResults').doc(docId).set({
 result: result,
 status: 'completed',
 processedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });

 console.log('Processing completed for:', filePath);

 } catch (error) {
 console.error('Error processing file:', error);

 // Store error result
 await admin.firestore().collection('processingResults').doc(docId).set({
 result: 'Processing failed: ' + error.message,
 status: 'error',
 processedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });
 }
});

async function processGloveDataAlgorithm(csvContent) {
 // Parse CSV content
 const lines = csvContent.split('\n');
 const headers = lines[0].split(',');
 const data = [];

 for (let i = 1; i < lines.length; i++) {
 if (lines[i].trim()) {
 const values = lines[i].split(',').map(v => parseFloat(v.trim()));
 if (values.length === headers.length) {
 data.push(values);
 }
 }
 }

 if (data.length === 0) {
 return "No valid data found in CSV file";
 }

 // Extract sensor data (assuming CSV format: Timestamp,Flex1-5,FSR1-5,IMU_X,IMU_Y,IMU_Z,IMU_Roll,IMU_Pitch,IMU_Yaw,BioAmp)
 const flexData = data.map(row => row.slice(1, 6)); // Flex1-5
 const fsrData = data.map(row => row.slice(6, 11)); // FSR1-5
 const imuData = data.map(row => row.slice(11, 17)); // IMU data
 const bioampData = data.map(row => row[17]); // BioAmp

 // YOUR ALGORITHM IMPLEMENTATION HERE
 const analysis = performAnalysis(flexData, fsrData, imuData, bioampData);

 return analysis;
}

function performAnalysis(flexData, fsrData, imuData, bioampData) {
 // Calculate averages
 const avgFlex = flexData.reduce((sum, row) => sum + row.reduce((a, b) => a + b, 0) / row.length, 0) / flexData.length;
 const maxForce = Math.max(...fsrData.flat());
 const avgForce = fsrData.reduce((sum, row) => sum + row.reduce((a, b) => a + b, 0) / row.length, 0) / fsrData.length;

 // Calculate movement variability
 const imuVariability = imuData.reduce((sum, row) => {
 const variance = row.reduce((acc, val, idx, arr) => {
 const mean = arr.reduce((a, b) => a + b, 0) / arr.length;
 return acc + Math.pow(val - mean, 2);
 }, 0) / row.length;
 return sum + variance;
 }, 0) / imuData.length;

 // Calculate muscle activity
 const bioampMean = bioampData.reduce((a, b) => a + b, 0) / bioampData.length;
 const bioampStd = Math.sqrt(bioampData.reduce((sum, val) => sum + Math.pow(val - bioampMean, 2), 0) / bioampData.length);

 // Generate scores
 const flexibilityScore = Math.min(10, Math.max(0, avgFlex / 10));
 const strengthScore = Math.min(10, Math.max(0, maxForce / 50));
 const coordinationScore = Math.min(10, Math.max(0, 10 - imuVariability / 100));
 const overallScore = (flexibilityScore + strengthScore + coordinationScore) / 3;

 // Generate recommendations
 let recommendations = [];
 if (flexibilityScore < 5) recommendations.push("Focus on flexibility exercises");
 if (strengthScore < 5) recommendations.push("Work on grip strength training");
 if (coordinationScore < 5) recommendations.push("Practice coordination exercises");
 if (recommendations.length === 0) recommendations.push("Maintain current exercise routine");

 return ` SMART GLOVE ANALYSIS REPORT

 SENSOR ANALYSIS:
• Flexibility Score: ${flexibilityScore.toFixed(1)}/10
• Grip Strength: ${strengthScore.toFixed(1)}/10 
• Coordination: ${coordinationScore.toFixed(1)}/10

 PERFORMANCE METRICS:
• Average Flex: ${avgFlex.toFixed(2)}
• Maximum Force: ${maxForce.toFixed(2)} N
• Average Force: ${avgForce.toFixed(2)} N
• Movement Variability: ${imuVariability.toFixed(2)}
• Muscle Activity: ${bioampStd.toFixed(2)}

 OVERALL SCORE: ${overallScore.toFixed(1)}/10

 RECOMMENDATIONS:
${recommendations.map(r => `• ${r}`).join('\n')}

⏱️ SESSION SUMMARY:
• Total Data Points: ${flexData.length}
• Session Duration: ${(flexData.length * 0.1).toFixed(1)} seconds
• Analysis Generated: ${new Date().toLocaleString()}`;
}
```

