const admin = require('firebase-admin');

admin.initializeApp();

// This function triggers automatically when a CSV file is uploaded to Firebase Storage
exports.processGloveData = functions.storage.object().onFinalize(async (object) => {
 const filePath = object.name;

 // Only process CSV files in the sessions folder
 if (!filePath.startsWith('sessions/') || !filePath.endsWith('.csv')) {
 console.log('Skipping non-session file:', filePath);
 return;
 }

 // Extract document ID from file path
 const docId = filePath.replace('sessions/', '').replace('.csv', '');

 try {
 console.log('Processing file:', filePath);

 // Set processing status in Firestore
 await admin.firestore().collection('processingResults').doc(docId).set({
 status: 'processing',
 startedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });

 // Download and process the CSV file
 const bucket = admin.storage().bucket();
 const file = bucket.file(filePath);
 const [csvData] = await file.download();

 console.log('Downloaded CSV, size:', csvData.length);

 // YOUR ALGORITHM GOES HERE
 const analysisResult = await processGloveDataWithYourAlgorithm(csvData.toString());

 // Store the results in Firestore
 await admin.firestore().collection('processingResults').doc(docId).set({
 result: analysisResult,
 status: 'completed',
 processedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });

 console.log('✅ Processing completed successfully for:', filePath);

 } catch (error) {
 console.error('❌ Error processing file:', error);

 // Store error in Firestore
 await admin.firestore().collection('processingResults').doc(docId).set({
 result: `Processing failed: ${error.message}`,
 status: 'error',
 processedAt: admin.firestore.FieldValue.serverTimestamp(),
 filePath: filePath
 });
 }
});

// YOUR MAIN ALGORITHM FUNCTION
async function processGloveDataWithYourAlgorithm(csvContent) {
 try {
 // Parse the CSV data
 const lines = csvContent.trim().split('\n');
 const headers = lines[0].split(',');

 console.log('CSV Headers:', headers);
 console.log('Total data rows:', lines.length - 1);

 // Parse data rows
 const sensorData = [];
 for (let i = 1; i < lines.length; i++) {
 const values = lines[i].split(',').map(val => parseFloat(val.trim()));
 if (values.length >= 17 && !values.some(isNaN)) { // Ensure we have all 17 sensor values
 sensorData.push({
 timestamp: values[0],
 flex: values.slice(1, 6), // Flex1-5
 fsr: values.slice(6, 11), // FSR1-5 
 imu: values.slice(11, 17), // IMU data (6 values)
 bioamp: values[17] || 0 // BioAmp
 });
 }
 }

 if (sensorData.length === 0) {
 return "❌ No valid sensor data found in the uploaded file.";
 }

 console.log('Valid sensor data points:', sensorData.length);

 // ANALYZE THE DATA (Replace this with your actual algorithm)
 const analysis = analyzeGloveData(sensorData);

 return analysis;

 } catch (error) {
 console.error('Error in algorithm:', error);
 return `❌ Algorithm error: ${error.message}`;
 }
}

// YOUR ACTUAL ANALYSIS ALGORITHM
function analyzeGloveData(sensorData) {
 // Extract sensor arrays for analysis
 const flexData = sensorData.map(d => d.flex);
 const fsrData = sensorData.map(d => d.fsr);
 const imuData = sensorData.map(d => d.imu);
 const bioampData = sensorData.map(d => d.bioamp);

 // CALCULATE METRICS

 // 1. Flexibility Analysis
 const avgFlexPerFinger = [0, 1, 2, 3, 4].map(fingerIndex => {
 const flexValues = flexData.map(row => row[fingerIndex]);
 return flexValues.reduce((a, b) => a + b, 0) / flexValues.length;
 });
 const overallFlexibility = avgFlexPerFinger.reduce((a, b) => a + b, 0) / 5;

 // 2. Grip Strength Analysis 
 const maxGripPerFinger = [0, 1, 2, 3, 4].map(fingerIndex => {
 const fsrValues = fsrData.map(row => row[fingerIndex]);
 return Math.max(...fsrValues);
 });
 const maxOverallGrip = Math.max(...maxGripPerFinger);
 const avgGripStrength = maxGripPerFinger.reduce((a, b) => a + b, 0) / 5;

 // 3. Movement Coordination (IMU Analysis)
 const imuVariability = imuData.reduce((totalVar, row) => {
 const variance = row.reduce((sum, val, idx, arr) => {
 const mean = arr.reduce((a, b) => a + b, 0) / arr.length;
 return sum + Math.pow(val - mean, 2);
 }, 0) / row.length;
 return totalVar + variance;
 }, 0) / imuData.length;

 // 4. Muscle Activity Analysis
 const bioampMean = bioampData.reduce((a, b) => a + b, 0) / bioampData.length;
 const bioampStd = Math.sqrt(
 bioampData.reduce((sum, val) => sum + Math.pow(val - bioampMean, 2), 0) / bioampData.length
 );

 // GENERATE SCORES (0-10 scale)
 const flexibilityScore = Math.min(10, Math.max(0, overallFlexibility / 10));
 const strengthScore = Math.min(10, Math.max(0, maxOverallGrip / 100));
 const coordinationScore = Math.min(10, Math.max(0, 10 - (imuVariability / 50)));
 const muscleActivityScore = Math.min(10, Math.max(0, bioampStd / 10));

 const overallScore = (flexibilityScore + strengthScore + coordinationScore + muscleActivityScore) / 4;

 // GENERATE RECOMMENDATIONS
 const recommendations = [];
 if (flexibilityScore < 4) recommendations.push("‍♂️ Focus on finger flexibility exercises");
 if (strengthScore < 4) recommendations.push(" Increase grip strength training");
 if (coordinationScore < 4) recommendations.push(" Practice hand coordination exercises");
 if (muscleActivityScore < 4) recommendations.push("⚡ Work on muscle activation patterns");
 if (recommendations.length === 0) recommendations.push(" Excellent performance! Maintain current routine");

 // GENERATE DETAILED REPORT
 const analysisReport = `
 **SMART GLOVE ANALYSIS REPORT**
━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━

 **PERFORMANCE SCORES:**
├─ Flexibility: ${flexibilityScore.toFixed(1)}/10 ${getScoreEmoji(flexibilityScore)}
├─ Grip Strength: ${strengthScore.toFixed(1)}/10 ${getScoreEmoji(strengthScore)}
├─ Coordination: ${coordinationScore.toFixed(1)}/10 ${getScoreEmoji(coordinationScore)}
└─ Muscle Activity: ${muscleActivityScore.toFixed(1)}/10 ${getScoreEmoji(muscleActivityScore)}

 **OVERALL SCORE: ${overallScore.toFixed(1)}/10** ${getScoreEmoji(overallScore)}

 **DETAILED METRICS:**
├─ Average Flexibility: ${overallFlexibility.toFixed(2)}°
├─ Maximum Grip Force: ${maxOverallGrip.toFixed(2)} N
├─ Average Grip Strength: ${avgGripStrength.toFixed(2)} N
├─ Movement Variability: ${imuVariability.toFixed(2)}
└─ Muscle Activity Level: ${bioampStd.toFixed(2)}

 **FINGER-BY-FINGER ANALYSIS:**
├─ Thumb: Flex ${avgFlexPerFinger[0].toFixed(1)}° | Grip ${maxGripPerFinger[0].toFixed(1)}N
├─ Index: Flex ${avgFlexPerFinger[1].toFixed(1)}° | Grip ${maxGripPerFinger[1].toFixed(1)}N 
├─ Middle: Flex ${avgFlexPerFinger[2].toFixed(1)}° | Grip ${maxGripPerFinger[2].toFixed(1)}N
├─ Ring: Flex ${avgFlexPerFinger[3].toFixed(1)}° | Grip ${maxGripPerFinger[3].toFixed(1)}N
└─ Pinky: Flex ${avgFlexPerFinger[4].toFixed(1)}° | Grip ${maxGripPerFinger[4].toFixed(1)}N

 **RECOMMENDATIONS:**
${recommendations.map(r => `• ${r}`).join('\n')}

⏱️ **SESSION SUMMARY:**
├─ Total Data Points: ${sensorData.length}
├─ Session Duration: ${(sensorData.length * 0.1).toFixed(1)} seconds
├─ Data Quality: ${sensorData.length > 1000 ? 'Excellent' : sensorData.length > 500 ? 'Good' : 'Fair'}
└─ Analysis Date: ${new Date().toLocaleString()}

━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
Generated by Smart Glove AI Analysis System
`;

 return analysisReport;
}

// Helper function to get emoji based on score
function getScoreEmoji(score) {
 if (score >= 8) return '';
 if (score >= 6) return '';
 if (score >= 4) return '';
 return '⚠️';
}
```

### **Step 4: Deploy Your Function**

```bash
# Deploy to Firebase
firebase deploy --only functions
```

### **Step 5: Set Up Firebase Storage Rules**

In the Firebase Console → Storage → Rules, add:

```javascript
rules_version = '2';
service firebase.storage {
 match /b/{bucket}/o {
 match /sessions/{fileName} {
 allow read, write: if request.auth != null;
 }
 }
}
```

### **Step 6: Set Up Firestore Rules**

In the Firebase Console → Firestore → Rules, add:

```javascript
rules_version = '2';
service cloud.firestore {
 match /databases/{database}/documents {
 match /processingResults/{docId} {
 allow read, write: if request.auth != null;
 }
 }
}
```

## **How Your System Will Work**

1. ** App**: Collects 2 minutes of glove data → Saves to CSV → Uploads to Firebase Storage
2. **☁️ Cloud**: Firebase Function automatically detects upload → Downloads CSV → Runs your algorithm → Saves results to Firestore
3. ** Results**: App polls Firestore → Gets results → Displays analysis in ResultScreen

## **Customize Your Algorithm**

In the `analyzeGloveData` function, you can:
- Add your own analysis logic
- Calculate custom metrics
- Generate specific recommendations
- Format the output however you want

The function receives clean sensor data and returns a formatted analysis string that appears in your app!

