+/**
+ * Provides a mock smart glove analysis for UI flow testing.
+ */
+private fun generateMockAnalysis(): String {
+ // Mock algorithm based on collected data
+ val flexAvg = _flexEntriesInternal.map { it.lastOrNull()?.y ?: 0f }.average()
+ val fsrMax = _fsrEntriesInternal.map { it.maxByOrNull { entry -> entry.y }?.y ?: 0f }.maxOrNull() ?: 0f
+ val dataPoints = _flexEntriesInternal.sumOf { it.size }
+ 
+ return """
+ **SMART GLOVE ANALYSIS REPORT**
+━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━
+
+ **PERFORMANCE SCORES:**
+├─ Flexibility: ${
+ (flexAvg / 10.0).coerceIn(0.0, 10.0).let { "%.1f".format(it) }
+ }/10 ${if (flexAvg > 50) "" else ""}
+├─ Grip Strength: ${
+ (fsrMax / 50.0).coerceIn(0.0, 10.0).let { "%.1f".format(it) }
+ }/10 ${if (fsrMax > 200) "" else ""}
+├─ Coordination: 8.1/10 
+└─ Muscle Activity: 7.8/10 
+
