 }
 }
 )
 SIGN_UP -> SignUpScreen(
 viewModel = authViewModel,
 onSignUpSuccess = {
 currentAuthScreen = SIGN_IN
 Log.d(TAG, "Signed up, navigating to SIGN_IN")
 },
 onNavigateToSignIn = { currentAuthScreen = SIGN_IN }
 )
 else -> { /* Should not happen if logic is correct */ }
 }
