// ... existing code ...
import androidx.compose.foundation.layout.Box
import androidx.compose.foundation.layout.Column
import androidx.compose.runtime.Composable
import androidx.compose.ui.geometry.Offset
import androidx.compose.ui.geometry.Size
import androidx.compose.ui.graphics.Brush
import androidx.compose.ui.graphics.StrokeCap
import androidx.compose.ui.graphics.drawscope.Stroke
import androidx.compose.ui.unit.dp
import org.json.JSONObject

// ... existing code ...

@Composable
fun FingerScoreSpinner(
 score: Int,
 label: String,
 gradient: Brush,
 modifier: Modifier = Modifier,
 size: Float = 100f
) {
 Box(modifier = modifier.size(size.dp), contentAlignment = Alignment.Center) {
 Canvas(modifier = Modifier.fillMaxSize()) {
 val stroke = 16f
 // Draw grey circle for the background
 drawArc(
 color = Color.LightGray,
 startAngle = 135f, // arc starts on the side
 sweepAngle = 270f,
 useCenter = false,
 style = Stroke(width = stroke, cap = StrokeCap.Round),
 size = Size(size - stroke, size - stroke),
 topLeft = Offset(stroke/2, stroke/2)
 )
 // Draw foreground gradient arc for the score
 drawArc(
 brush = gradient,
 startAngle = 135f,
 sweepAngle = 270f * (score / 100f),
 useCenter = false,
 style = Stroke(width = stroke, cap = StrokeCap.Round),
 size = Size(size - stroke, size - stroke),
 topLeft = Offset(stroke/2, stroke/2)
 )
 }
 // Center text with percent
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 Text(
 "$score%",
 style = MaterialTheme.typography.titleLarge,
 fontWeight = _root_ide_package_.androidx.compose.ui.text.font.FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary
 )
 Text(
 label,
 style = MaterialTheme.typography.labelMedium,
 color = MaterialTheme.colorScheme.onBackground.copy(alpha = 0.6f)
 )
 }
 }
}

// ... existing code ...

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ResultScreen(
 resultData: String, // The raw string result from Jetson Nano/cloud
 onBackToMain: () -> Unit,
 isDarkTheme: Boolean,
 onToggleTheme: () -> Unit,
 onSignOut: () -> Unit
) {
 GloveAppTheme(darkTheme = isDarkTheme) {
 Scaffold(
 topBar = {
 TopAppBar(
 title = { Text("Session Result", color = MaterialTheme.colorScheme.onPrimaryContainer) },
 colors = TopAppBarDefaults.topAppBarColors(containerColor = MaterialTheme.colorScheme.primaryContainer),
 actions = {
 IconButton(onClick = onToggleTheme) {
 Icon(
 imageVector = if (isDarkTheme) Icons.Default.LightMode else Icons.Default.DarkMode,
 contentDescription = "Toggle theme",
 tint = MaterialTheme.colorScheme.onPrimaryContainer
 )
 }
 IconButton(onClick = onSignOut) {
 Icon(
 imageVector = Icons.Default.Logout,
 contentDescription = "Sign out",
 tint = MaterialTheme.colorScheme.onPrimaryContainer
 )
 }
 },
 navigationIcon = {
 IconButton(onClick = onBackToMain) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back",
 tint = MaterialTheme.colorScheme.onPrimaryContainer
 )
 }
 }
 )
 }
 ) { paddingValues ->
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .background(MaterialTheme.colorScheme.background)
 .verticalScroll(rememberScrollState())
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Top
 ) {
 Spacer(Modifier.height(16.dp))
 Text(
 text = "Finger Health Scores",
 style = MaterialTheme.typography.headlineLarge,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary,
 textAlign = TextAlign.Center
 )
 Spacer(Modifier.height(32.dp))
 // --- NEW UI: Rainbow Arc Spinners ---
 val fingerLabels = listOf("Thumb", "Index", "Middle", "Ring", "Little")
 // Parse JSON scores: expect {"scores":[90,80,70,60,50]}
 val fingerScores: List<Int> = try {
 JSONObject(resultData).getJSONArray("scores").let { arr ->
 List(arr.length()) { idx -> arr.getInt(idx) }
 }
 } catch (_: Exception) {
 List(5) { 0 }
 }
 // Gradients for each finger (use rainbow-like colors)
 val gradients = listOf(
 Brush.sweepGradient(listOf(Color.Magenta, Color.Cyan, Color.Magenta)), // Thumb
 Brush.sweepGradient(listOf(Color(0xFF4CAF50), Color.Blue, Color(0xFF4CAF50))), // Index
 Brush.sweepGradient(listOf(Color(0xFFFFEB3B), Color.Yellow, Color(0xFFFFEB3B))), // Middle
 Brush.sweepGradient(listOf(Color(0xFFFF9800), Color.Red, Color(0xFFFF9800))), // Ring
 Brush.sweepGradient(listOf(Color(0xFF9C27B0), Color.Green, Color(0xFF9C27B0))) // Little
 )
 // Arc layout math: positions relative to the parent box
 Box(modifier = Modifier.fillMaxWidth().height(180.dp)) {
 // Place each spinner along a semicircular arc
 val degrees = listOf(200f, 230f, 270f, 310f, 340f) // Equally spaced arc
 val radius = 70.dp.toPx() + 30 // Customizable arc radius
 for (i in 0..4) {
 Box(
 modifier = Modifier
 .size(100.dp)
 .offset {
 // Polar to Cartesian for arc
 val rad = Math.toRadians(degrees[i].toDouble())
 val x = (radius * kotlin.math.cos(rad)).toInt()
 val y = (radius * kotlin.math.sin(rad)).toInt()
 IntOffset(x, y)
 }
 .align(Alignment.Center)
 ) {
 FingerScoreSpinner(
 score = fingerScores[i],
 label = fingerLabels[i],
 gradient = gradients[i],
 size = 90f
 )
 }
 }
 }
 Spacer(Modifier.height(32.dp))
 Button(
 onClick = onBackToMain,
 modifier = Modifier.fillMaxWidth()
 ) {
 Text("Back to Main Screen")
 }
 }
 }
 }
}
